script:
frappe.ui.form.on('Order Line',  {
	validate: function(frm) {

	}
})

frappe.ui.form.on("Order Line", "price", function(frm, cdt, cdn) {

    total = flt(frm.doc.price) * flt(frm.doc.quantity)

    frm.set_value("sub_total",total)
});

frappe.ui.form.on("Order Line", "price", function(frm, cdt, cdn) {

     var order_line = frm.doc.order_line
     var total = 0
     for(var i in order_line) {
      total += + order_line[i].price * order_line[i].quantity
      }

    frm.set_value("total",total)
});
