script:
frappe.ui.form.on('Order Line',  {
	validate: function(frm) {

	}
})

frappe.ui.form.on("Order Line", "price", function(frm, cdt, cdn) {

    total = flt(frm.doc.price) * flt(frm.doc.quantity)

    frm.set_value("sub_total",total)
});

frappe.ui.form.on("Order Line", "item", function(frm, cdt, cdn) {
    frm.set_value("price", 120)
});

frappe.ui.form.on("Order Line", "quantity", function(frm, cdt, cdn) {
    console.log(frm)
     var order_line = frm.doc.order_line
     var total = 0
     for(var i in order_line) {
      total += + order_line[i].price * order_line[i].quantity
      }

    frm.set_value("total",total)
});

frappe.ui.form.on("Order Line", "item", function(frm, cdt, cdn) {
    console.log(frm)
    console.log(cdt)
    console.log(cdn)
     var order_line = frm.doc.order_line
     var total = 0
     for(var i in order_line) {
         frappe.call({
			method: "frappe.client.get",
			args: {
				doctype: "Items",
				filters: {"name":order_line[i].item}    //user is current user here
			},
			callback: function(r) {
				frappe.model.set_value(order_line[i].doctype, order_line[i].name, "price", r.message.price)
				frappe.model.set_value(order_line[i].doctype, order_line[i].name, "satuan", r.message.unit)
			}
		})

     }
     frm.refresh_field('details');

});
